import ChartBar from './ChartBar'

import './Chart.css'

const Chart=(props)=>{
    const valueFinder=props.datapoints.map(data=>data.value)
    const maxDatapoint=Math.max(...valueFinder)
    console.log(maxDatapoint)
    return(
        <div>
        {props.datapoints.map((datapoint)=>{
            <ChartBar id={datapoint.label} value={datapoint.value} maxValue={maxDatapoint}/>
        })}
        </div>
    )

}
export default Chart
