import React,{useState} from 'react';
import FormControl from './components/Form/FormControl';
import UserDisplay from './components/UserDisplay/UserDisplay'


function App() {

  const dummy_users=[
    {name:'Yash',age:'19',key:'1'},
    {name:'Fransis', age:'25',key:'2'}
  ]

  const [users_list,setUserList]=useState(dummy_users)

  const newUserAdder=(user)=>{

    const new_users_list=[
      ...users_list,
      user
    ]
    setUserList(new_users_list)


  }

  return (
    <div>
      <FormControl onFormSubmit={newUserAdder} />
      <UserDisplay data={users_list}/>
    </div>
  );
}

export default App;
