import React,{useState} from "react"
import './FormControl.css'

const FormControl=(props)=>{
    const [name,setName]=useState('')
    const [age,setAge]=useState('')

    const formSubmitter=(e)=>{
        e.preventDefault()
        const new_uesr={name,age}
        setAge('')
        setName('')
        props.onFormSubmit(new_uesr)


    }

    const nameChangeHandler=(e)=>{
        setName(e.target.value)
    }

    const ageChangeHandler=(e)=>{
        setAge(e.target.value)
    }


    return(
        <form className="formcontrol" onSubmit={formSubmitter}>
        <div className='formcontrol__field'>
            <label className="formcontrol__title">Username</label>
            <input type='text' value={name} onChange={nameChangeHandler} className="formcontrol__inputbox"></input>
        </div>
        <div className='formcontrol__field'>
            <label className="formcontrol__title">Age(Years)</label>
            <input type='text' value={age} onChange={ageChangeHandler} className="formcontrol__inputbox"></input>
        </div>
        <button type='submit' className="formcontrol__button" >Add User</button>
    </form>
    )

}

export default FormControl
