import React, { useContext, useState } from 'react'
import Cart from '../../cartholder/Cart'

const NavBar = () => {
  const [cartupdate, setCartUpdate] = useState(0)
  const cartobject = useContext(Cart)

  if (cartobject.changeCart) {
    let number = cartupdate
    for (let i of cartobject.cart) {
      number += parseInt(i.quantity)
    }
    console.log(number, 'kpqdkwpdk')
    cartobject.changeCart = false
    setCartUpdate(number)
  }

  return (
    <div className='navbar'>
      <h1 className='navbar__title'>Food App</h1>
      <button className='navbar__btn'>
        <p className='navbar__btn__title'>
          Cart
        </p>
        <p className='navbar__btn__feature'>
          {cartupdate}
        </p>
      </button>
    </div>

  )
}

export default NavBar
