import React, { useState } from 'react'

const menubar = [
  {
    name: 'Pizza',
    price: '40.99'
  },
  {
    name: 'Burger',
    price: '12.99'
  }
]

const MenuList = () => {
  const [quantitiy, setQuantity] = useState('1')
  const quantityChange = (e) => {
    setQuantity(e.target.value)
  }
  return (
    <div className='menu_container'>
      {menubar.map((menuitem) => {
         return (
           <div className='menu__item'>
             <h3 className='menu__title'>{menuitem.name}</h3>
             <p className='menu__price'>
               {menuitem.price}
             </p>
             <input type='number' value={quantitiy} onChange={quantityChange}>
             </input>
           </div>
         )})}
    </div>

  )
}

export default MenuList
