import {createStore} from 'redux'
const reducerFunction = (state={counter:0},action) => {
    if (action.type === 'inc') {
        return {
            counter:state.counter+1
        }
    }
    if (action.type === 'dec') {
        return {
            counter:state.counter-1
        }
    }
    return state
}

const store = createStore(reducerFunction)

export default store
